import {checkFramebuffer} from "../../../../Util/FramebufferCheck";
import {MainController} from "../../../MainController";
import {DrawMesh} from "../../../../Render/DrawMesh";

/*
USED FOR RENDERING THE SAME WAY WE DID RENDER THE NORNAMLL SCENE
 */
export class TransparencyPassStorage {

    // Generated By Geometry Pass!
    transparency_framebuffer: WebGLFramebuffer;
    blend_texture: WebGLTexture;
    blend_transparency_texture: WebGLTexture;
    t_depth_texture: WebGLTexture;

    setupFrame() {

    }

    bindTransparencyFramebufferAndShader(GL: WebGL2RenderingContext) {
        GL.bindFramebuffer(GL.FRAMEBUFFER, this.transparency_framebuffer);
        MainController.ShaderController.useTransparencyShader();
    }


    constructor(GL: WebGL2RenderingContext, SIZE: number) {

        const INTERN_FORMAT: GLint = GL.RGBA32F;
        const FILTER = GL.NEAREST;

        ////////////////////////////////////////////
        // TRANSPARENCY BLEND PASS
        ////////////////////////////////////////////
        this.transparency_framebuffer = GL.createFramebuffer();
        GL.bindFramebuffer(GL.FRAMEBUFFER, this.transparency_framebuffer);

        this.t_depth_texture = this.createTexture(GL, GL.DEPTH_COMPONENT32F, FILTER, SIZE);
        GL.framebufferTexture2D(GL.FRAMEBUFFER, GL.DEPTH_ATTACHMENT, GL.TEXTURE_2D, this.t_depth_texture, 0);

        this.blend_texture = this.createTexture(GL, INTERN_FORMAT, FILTER, SIZE);
        GL.framebufferTexture2D(GL.FRAMEBUFFER, GL.COLOR_ATTACHMENT0, GL.TEXTURE_2D, this.blend_texture, 0);

        this.blend_transparency_texture = this.createTexture(GL, INTERN_FORMAT, FILTER, SIZE);
        GL.framebufferTexture2D(GL.FRAMEBUFFER, GL.COLOR_ATTACHMENT1, GL.TEXTURE_2D, this.blend_transparency_texture, 0);

        GL.drawBuffers([
            GL.COLOR_ATTACHMENT0,
            GL.COLOR_ATTACHMENT1,
        ]);
        checkFramebuffer(GL, this.transparency_framebuffer);

        ////////////////////////////////////////////
        // SETUP DRAW BUFFER
        // CHECK STATUS
        // & UNBIND THE SHIT
        ////////////////////////////////////////////


        //  MainController.Log.info("PassStorage", "finished: GeometryPassStorage");

        // reset used bindings
        GL.bindTexture(GL.TEXTURE_2D, null);
        GL.bindFramebuffer(GL.FRAMEBUFFER, null);

    }

    private createTexture(GL: WebGL2RenderingContext, internal_format: GLint, filter: GLint, size: number) {
        const LEVEL = 1;
        const texture = GL.createTexture();
        GL.bindTexture(GL.TEXTURE_2D, texture);
        GL.pixelStorei(GL.UNPACK_FLIP_Y_WEBGL, 0);
        GL.texStorage2D(
            GL.TEXTURE_2D,
            LEVEL,
            internal_format,
            size,
            size
        );
        GL.texParameteri(GL.TEXTURE_2D, GL.TEXTURE_MIN_FILTER, filter);
        GL.texParameteri(GL.TEXTURE_2D, GL.TEXTURE_MAG_FILTER, filter);
        return texture;
    }

}